import React, { useState } from 'react';

function App() {
  const [inputValue, setInputValue] = useState('');
  const [responseData, setResponseData] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');

    try {
      const response = await fetch('http://a621170d5d88745a1843f12382898e43-2134758004.eu-west-2.elb.amazonaws.com:3003/save-data', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ inputValue }),
      });

      if (!response.ok) {
        throw new Error('Error saving data');
      }

      const data = await response.json();
      setResponseData(data.id);
    } catch (error) {
      setError('Valami nem jo gecc!');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div>
      <h1>Basic_Express_For_CI-CD</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Input:
          <input
            type="text"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
          />
        </label>
        <button type="submit" disabled={isLoading}>
          {isLoading ? 'Loading...' : 'Submit'}
        </button>
      </form>

      {error && <p>{error}</p>}
      {responseData && <p>Response: {responseData}</p>}
    </div>
  );
}

export default App;
